// üî∏ Zona y fechas mostradas
const zona = $('Switch1').first()?.json?.zona_detectada || "";
const offset = $('Switch1').first()?.json?.fechas_mostradas 
            || $('Code4').first()?.json?.fechas_mostradas 
            || 0;

const registros = $input.all().map(i => i.json);

// üî§ Normalizar texto
const normalizar = (texto) =>
  texto?.toLowerCase()
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "")
    .replace(/[^a-z0-9 ]/g, "")
    .trim() || "";

const zonaNormalizada = normalizar(zona);

// üîç Filtrar registros por zona
const filtrados = registros.filter(r => {
  const destinoNormalizado = normalizar(r.destino);
  return destinoNormalizado.includes(zonaNormalizada);
});

// üìÜ Ciclo de corte
const hoy = new Date();
const diaHoy = hoy.getDay();
let fechaInicio = new Date(hoy);

if (diaHoy >= 3 && diaHoy <= 5) {
  fechaInicio.setDate(hoy.getDate() + ((6 + 7 - diaHoy) % 7)); // Siguiente s√°bado
} else {
  fechaInicio.setDate(hoy.getDate() + ((3 + 7 - diaHoy) % 7)); // Siguiente mi√©rcoles
}

const diasOrden = ["domingo", "lunes", "martes", "mi√©rcoles", "jueves", "viernes", "s√°bado"];

function proximaFecha(diaTexto, desdeFecha, repeticiones = 0) {
  const indexObjetivo = diasOrden.indexOf(diaTexto.toLowerCase());
  const base = new Date(desdeFecha);
  const actual = base.getDay();
  const diasSumar = ((indexObjetivo - actual + 7) % 7) + repeticiones * 7;
  base.setDate(base.getDate() + diasSumar);
  return base;
}

// üß† Generar todas las fechas posibles
let resultados = [];
let repeticiones = 0;
while (resultados.length < offset + 10) {
  for (const r of filtrados) {
    const fecha = proximaFecha(r.dia, fechaInicio, repeticiones);
    resultados.push({
      fecha,
      fechaFormateada: fecha.toLocaleDateString("es-SV", {
        weekday: "long", year: "numeric", month: "long", day: "numeric"
      }),
      destino: r.destino
    });
  }
  repeticiones++;
}

// üîÅ Aplicar offset y limitar a 3 nuevas fechas
const seleccion = resultados
  .filter(f => f.fecha >= fechaInicio)
  .sort((a, b) => a.fecha - b.fecha)
  .slice(offset, offset + 3);

// ‚úèÔ∏è Armar mensaje visual
const mensaje = seleccion.map((f, i) =>
  `üìå ${f.fechaFormateada}\n${f.destino}`
).join("\n\n");

// ‚úÖ Devolver mensaje y nuevo offset actualizado
return [{
  json: {
    text: mensaje,
    fechas_mostradas: offset + seleccion.length
  }
}];

const raw = $input.first().json.ubicacion || "";

// Intentar primero con formato decimal: "13.696472, -89.202333"
let regexDecimal = /^(-?\d+(\.\d+)?),\s*(-?\d+(\.\d+)?)$/;
let matchDecimal = raw.match(regexDecimal);

if (matchDecimal) {
  const lat = parseFloat(matchDecimal[1]);
  const lng = parseFloat(matchDecimal[3]);
  return [{ json: { lat, lng, formato: "decimal" } }];
}

// Si no es decimal, intentar con formato DMS: "13¬∞41'47.3\"N 89¬∞12'08.4\"W"
let regexDMS = /(\d{1,3})¬∞(\d{1,2})'(\d{1,2}(?:\.\d+)?)"([NS])\s+(\d{1,3})¬∞(\d{1,2})'(\d{1,2}(?:\.\d+)?)"([EW])/;
let matchDMS = raw.match(regexDMS);

if (matchDMS) {
  function convertToDecimal(deg, min, sec, dir) {
    let decimal = Number(deg) + Number(min) / 60 + Number(sec) / 3600;
    return (dir === "S" || dir === "W") ? -decimal : decimal;
  }

  const lat = convertToDecimal(matchDMS[1], matchDMS[2], matchDMS[3], matchDMS[4]);
  const lng = convertToDecimal(matchDMS[5], matchDMS[6], matchDMS[7], matchDMS[8]);

  return [{ json: { lat, lng, formato: "dms" } }];
}

// Si ninguno de los dos formatos es v√°lido
return [{
  json: { error: "Ubicaci√≥n inv√°lida o no se pudo interpretar" }
}];

// üìÖ Obtener fecha y hora actual
const fecha_hora = new Date().toLocaleString("es-SV", {
  timeZone: "America/El_Salvador",
  hour12: true
});

// üí¨ Mensaje original del usuario desde el nodo Edit Fields
const mensaje_usuario = $('Edit Fields').first().json.Texto || "Mensaje no disponible";

// üîÅ Reunir todas las respuestas generadas por los agentes (text)
const textos = $input.all()
  .map(item => item.json.text)
  .filter(Boolean);

// üßæ Concatenar si hay m√∫ltiples respuestas
const respuesta_agente = textos.join('\n---\n') || "Sin respuesta";

// üîÄ Capturar el bloque output de forma segura (solo si es objeto)
const output_extra = $input.first().json.output;
const output_validado = output_extra && typeof output_extra === 'object'
  ? output_extra
  : { output_bruto: output_extra }; // Si es texto, lo guarda como propiedad limpia llamada 'output_bruto'

// ‚úÖ Devolver el JSON para el historial en Google Sheets incluyendo output validado
return [
  {
    json: {
      fecha_hora,
      mensaje_usuario,
      respuesta_agente,
      ...output_validado // Solo si es objeto, si es texto va dentro de output_bruto
    }
  }
];
